import React, { useState } from 'react';
import './FacilityBookingApp.css';

const facilities = [
  {
    name: 'Clubhouse',
    slots: [
      { time: '10:00 - 16:00', rate: 100 },
      { time: '16:00 - 22:00', rate: 500 },
    ],
  },
  {
    name: 'Tennis Court',
    slots: [{ time: '00:00 - 24:00', rate: 50 }],
  },
];

const App = () => {
  const [bookings, setBookings] = useState([]);

  const handleBooking = (facilityName, date, slot) => {
    const existingBooking = bookings.find(
      (booking) =>
        booking.facility === facilityName && booking.date === date && booking.slot === slot
    );

    if (existingBooking) {
      alert('Booking Failed, Already Booked');
      return;
    }

    const facility = facilities.find((facility) => facility.name === facilityName);
    const slotDetails = facility.slots.find((s) => s.time === slot);
    const bookingAmount = slotDetails.rate;

    const newBooking = {
      facility: facilityName,
      date,
      slot,
      amount: bookingAmount,
    };

    setBookings([...bookings, newBooking]);
    alert(`Booked, Rs. ${bookingAmount}`);
  };

  return (
    <div className="facility-booking-app">
      <h1>Facility Booking</h1>
      <div className="facilities">
        {facilities.map((facility) => (
          <div className="facility" key={facility.name}>
            <h3>{facility.name}</h3>
            {facility.slots.map((slot) => (
              <button
                key={slot.time}
                onClick={() => handleBooking(facility.name, '26-10-2020', slot.time)}
              >
                {slot.time}
              </button>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
