import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
// import "./FacilityBookingApp.css";

const facilities = [
  {
    name: "Clubhouse",
    slots: [
      { time: "10:00 - 16:00", rate: 100 },
      { time: "16:00 - 22:00", rate: 500 },
    ],
  },
  {
    name: "Tennis Court",
    slots: [{ time: "00:00 - 24:00", rate: 50 }],
  },
];

const FacilityBookingApp = () => {
  const [selectedFacility, setSelectedFacility] = useState("");
  const [selectedDate, setSelectedDate] = useState(null);
  const [selectedSlot, setSelectedSlot] = useState("");
  const [bookings, setBookings] = useState([]);

  const handleBooking = () => {
    if (!selectedFacility || !selectedDate || !selectedSlot) {
      alert("Please select a facility, date, and time slot.");
      return;
    }

    const existingBooking = bookings.find(
      (booking) =>
        booking.facility === selectedFacility &&
        booking.date === selectedDate.toISOString() &&
        booking.slot === selectedSlot
    );

    if (existingBooking) {
      alert("Booking Failed, Already Booked");
      return;
    }

    const facility = facilities.find(
      (facility) => facility.name === selectedFacility
    );
    const slotDetails = facility.slots.find((s) => s.time === selectedSlot);
    const bookingAmount = slotDetails.rate;

    const newBooking = {
      facility: selectedFacility,
      date: selectedDate.toISOString(),
      slot: selectedSlot,
      amount: bookingAmount,
    };

    setBookings([...bookings, newBooking]);
    alert(`Booked, Rs. ${bookingAmount}`);
  };

  return (
    <div className="facility-booking-app">
      <h1>Facility Booking</h1>
      <div className="booking-form">
        <div className="facility-dropdown">
          <label>Select Facility:</label>
          <select onChange={(e) => setSelectedFacility(e.target.value)}>
            <option value="">--Select Facility--</option>
            {facilities.map((facility) => (
              <option key={facility.name} value={facility.name}>
                {facility.name}
              </option>
            ))}
          </select>
        </div>
        <div className="datepicker-container">
          <label>Select Date:</label>
          <DatePicker
            selected={selectedDate}
            onChange={(date) => setSelectedDate(date)}
          />
        </div>
        {selectedFacility && (
          <div className="slots-dropdown">
            <label>Select Time Slot:</label>
            <select onChange={(e) => setSelectedSlot(e.target.value)}>
              <option value="">--Select Time Slot--</option>
              {facilities
                .find((facility) => facility.name === selectedFacility)
                .slots.map((slot) => (
                  <option key={slot.time} value={slot.time}>
                    {slot.time}
                  </option>
                ))}
            </select>
          </div>
        )}
        <button onClick={handleBooking}>Book Facility</button>
      </div>
    </div>
  );
};

export default FacilityBookingApp;
